import {TaskType} from "./App";
import {Button} from "./Button";

type PropsType = {
	title: string
	tasks: TaskType[]
}

export const Todolist = ({title, tasks}: PropsType) => {

	return (
		<div>
			<h3>{title}</h3>
			<div>
				<input/>
				<Button title={'+'}/>
			</div>
			{
				!tasks.length
					? <p>Тасок нет</p>
					: <ul>
						{tasks.map((task) => {
							return <li key={task.id}><input type="checkbox" checked={task.isDone}/><span>{task.title}</span></li>
						})}
					</ul>
			}
			<div>
				<Button title={'All'}/>
				<Button title={'Active'}/>
				<Button title={'Completed'}/>
			</div>
		</div>
	)
}
