import {TypeFilteredTasks} from './App';

type ButtonPropsType = {
	title: string
	onClick: () => void
	filter: TypeFilteredTasks
}

export const Button = ({title, onClick, filter}: ButtonPropsType) => {
	const onClickHandler = () => {
		onClick();
	}

	if(filter === 'all') {
		return (
			<button className="active" onClick={onClickHandler}>{title}</button>
		)
	}

	if(filter === 'active') {
		return (
			<button className="active" onClick={onClickHandler}>{title}</button>
		)
	}

	if(filter === 'completed') {
		return (
			<button className="active" onClick={onClickHandler}>{title}</button>
		)
	}
}
