import './App.css';
import {Todolist} from './Todolist';
import {useState} from 'react';
import {v1} from 'uuid';

export type TaskType = {
  id: string
  title: string
  isDone: boolean
}

export type TypeFilteredTasks = 'all' | 'active' | 'completed';

function App() {
  const [tasks, setTasks] = useState<Array<TaskType>>([
    {id: v1(), title: 'HTML&CSS', isDone: true},
    {id: v1(), title: 'JS', isDone: true},
    {id: v1(), title: 'ReactJS', isDone: false},
    {id: v1(), title: 'Redux', isDone: false},
    {id: v1(), title: 'Typescript', isDone: false},
    {id: v1(), title: 'RTK query', isDone: false},
  ]);
  const [filter, setFilter] = useState<TypeFilteredTasks>('active');

  const removeTask = (tasksId: number) => {
    setTasks(tasks.filter(t => t.id != tasksId));
  }
  const addTask = (title: string) => {
    const task = {id: v1(), title, isDone: false};

    setTasks([task, ...tasks]);
  }

  let tasksForTodolist = tasks;

  if (filter === 'active') {
    tasksForTodolist = tasks.filter(t => t.isDone);
  }
  if (filter === 'completed') {
    tasksForTodolist = tasks.filter(t => !t.isDone);
  }

  const filteredTask = (filter: TypeFilteredTasks) => {
    setFilter(filter);
  }

  return (
    <div className="App">
      <Todolist
        title="What to learn"
        tasks={tasksForTodolist}
        removeTask={removeTask}
        addTask={addTask}
        filteredTask={filteredTask}
        filter={filter}
      />
    </div>
  );
}

export default App;
