import {TaskType, TypeFilteredTasks} from '../App';
import {Button} from "../Button";
import {useState} from 'react';
import s from './Todolist.module.css';

type PropsType = {
	title: string
	tasks: TaskType[]
	removeTask: (taskId: string) => void
	addTask: (title: string) => void
	filter: TypeFilteredTasks
}

export const Todolist = ({title, tasks, removeTask, addTask, filteredTask, filter}: PropsType) => {
	const [value, setValue] = useState('');
	console.log('filter: ', filter);

	const onChangeHandler = (e) => {
		setValue(e.currentTarget.value);
	}

	const addTaskHandler = () => {
		if(value.trim()) {
			addTask(value);
			setValue('');
		}
	}

	const filteredTaskHand = (filter: TypeFilteredTasks) => {
		filteredTask(filter);
	}

	return (
		<div>
			<h3>{title}</h3>
			<div>
				<input onChange={onChangeHandler} value={value}/>
				<Button title={'+'} onClick={addTaskHandler}/>
			</div>
			{
				tasks.length === 0
					? <p>Тасок нет</p>
					: <ul>
						{tasks.map((task) => {
							const removeTaskHandler = () => {
								removeTask(task.id);
							}

							return (
								<li key={task.id}>
									<input type="checkbox" checked={task.isDone}/>
									<span>{task.title}</span>
									<Button title={'x'} onClick={removeTaskHandler}/>
								</li>
							)
						})}
					</ul>
			}
			<div>
				<Button className={filter === 'all' ? s.active : ''} tonClick={() => filteredTaskHand('all')}/>
				<Button className={filter === 'active' ? s.active : ''} onClick={() => filteredTaskHand('active')}/>
				<Button className={filter === 'completed' ? s.active : ''} onClick={() => filteredTaskHand('completed')}/>
			</div>
		</div>
	)
}
