import React, {useState} from 'react';
import './App.css';
import {Todolist} from './Todolist';
import {v1} from 'uuid';

//Create
//Read(filter, sort, search, view mode)
//Update
//Delete

export type TaskType = {
  id: string
  title: string
  isDone: boolean
}

export type TodolistType = {
  id: string
  title: string
  filter: FilterValuesType
}

export type FilterValuesType = 'all' | 'active' | 'completed';
export type TasksStateType = {
  [todolistId: string]: TaskType[]
}

function App() {
  //BLL:
  const todolistId_1 = v1();
  const todolistId_2 = v1();
  const [todolists, setTodolists] = useState<Array<TodolistType>>([
    {
      id: todolistId_1,
      title: 'What to learn',
      filter: 'all',
    },
    {
      id: todolistId_2,
      title: 'What to bye',
      filter: 'active',
    },
  ]);

  const [tasks, setTasks] = useState<TasksStateType>({
    [todolistId_1]: [
      {id: v1(), title: 'HTML&CSS', isDone: true},
      {id: v1(), title: 'JS/TS', isDone: false},
      {id: v1(), title: 'REDUX', isDone: true},
    ],
    [todolistId_2]: [
      {id: v1(), title: 'Beer', isDone: true},
      {id: v1(), title: 'Cheeps', isDone: true},
      {id: v1(), title: 'Whiskey', isDone: true},
      {id: v1(), title: 'Cola', isDone: true},
    ]
  });


  const removeTask = (taskId: string, todolistId: string) => {
    setTasks({...tasks, [todolistId]: tasks[todolistId].filter(t => t.id !== taskId)});
  }

  const addTask = (title: string, todolistId: string) => {
    const newTask: TaskType = {
      title: title,
      isDone: false,
      id: v1()
    }

    setTasks({...tasks, [todolistId]: [...tasks[todolistId], newTask]})
  }

  const setTaskNewStatus = (taskId: string, newStatus: boolean, todolistId: string) => {
    setTasks({...tasks, [todolistId]: [...tasks[todolistId].map(t => t.id === taskId ? {...t, isDone: newStatus} : t)]});
  }


  //GUI:
  const changeTodolistFilter = (newFilter: FilterValuesType, todolistId: string) => {
    setTodolists(todolists.map(t => t.id === todolistId ? {...t, filter: newFilter} : t));
  }

  return (
    <div className="App">
      {
        todolists.map(t => {
          let filteredTasks: Array<TaskType> = tasks[t.id];
          if (t.filter === 'active') {
            filteredTasks = filteredTasks.filter(t => !t.isDone);
          }
          if (t.filter === 'completed') {
            filteredTasks = filteredTasks.filter(t => t.isDone);
          }

          return (
            <Todolist
              key={t.id}
              todolistId={t.id}
              title={t.title}
              tasks={filteredTasks}
              filter={t.filter}
              addTask={addTask}
              removeTask={removeTask}
              changeTodolistFilter={changeTodolistFilter}
              setTaskNewStatus={setTaskNewStatus}
            />
          )
        })
      }
    </div>
  );
}

export default App;
