import React, {useState} from 'react';
import './App.css';
import { Todolist } from './Todolist';
import { v1 } from 'uuid';

//Create
//Read(filter, sort, search, view mode)
//Update
//Delete

export type TaskType = {
    id: string
    title: string
    isDone: boolean
}

export type TodolistType = {
	id: string
	title: string
	filter: FilterValuesType
	
}

export type FilterValuesType = "all" | "active" | "completed"
function App() {

    console.log(v1());

    //BLL:
    const todolistTitle = "What to learn"
    const [tasks, setTasks] = React.useState<TaskType []>([   //initial state    #123
        { id: v1(), title: "HTML&CSS", isDone: true },
        { id: v1(), title: "JS/TS", isDone: true },
        // { id: v1(), title: "REDUX", isDone: false },
        { id: v1(), title: "REDUX", isDone: true },
    ])


    const removeTask = (taskId: string) => {
        //1. #234 => new array
        const nextState: Array<TaskType> = tasks.filter(t => t.id !== taskId)
        //2. set state
        setTasks(nextState)
    }
    const addTask = (title: string) => {
        const newTask: TaskType = {
            title: title,
            isDone: false,
            id: v1()
        }
        const copyState = [...tasks, newTask]
        setTasks(copyState)
    }
    const setTaskNewStatus = (taskId: string, newStatus: boolean) => {
        const nextState: Array<TaskType> = tasks.map(t => t.id === taskId ? {...t, isDone: newStatus} : t)
        setTasks(nextState)
    }






    //GUI:
    const [filter, setFilter] = useState<FilterValuesType>("all")

    let filteredTasks:  Array<TaskType> = tasks
    if(filter === "active"){
        filteredTasks = tasks.filter(t => t.isDone === false)
    }
    if(filter === "completed"){
        filteredTasks = tasks.filter(t => t.isDone === true)
    }

    const changeFilter = (newFilter: FilterValuesType) => {
        setFilter(newFilter)
    }
    return (
        <div className="App">
            <Todolist
                title={todolistTitle}
                tasks={filteredTasks}
                filter={filter}
                addTask={addTask}
                removeTask={removeTask}
                changeFilter={changeFilter}
                setTaskNewStatus={setTaskNewStatus}
            />


            {/* Todolist({title: "What to learn"}) */}
            {/* Todolist({title: "What to buy"}) */}
            {/* <div className="todolist">
                <h3>What to buy</h3>
                <div>
                    <input/>
                    <button>+</button>
                </div>
                <ul>
                    <li><input type="checkbox" checked={true}/> <span>Milk</span></li>
                    <li><input type="checkbox" checked={true}/> <span>Bread</span></li>
                    <li><input type="checkbox" checked={false}/> <span>Butter</span></li>
                </ul>
                <div>
                    <button>All</button>
                    <button>Active</button>
                    <button>Completed</button>
                </div>
            </div> */}
        </div>
    );
}

export default App;
